import React from 'react';

// Import all the individual section components
// Fixed: Reverted import extensions to .jsx, as this is standard for React components
// and likely where the actual component files are located.
import Header from './components/Header.jsx';
import HeroSection from './components/HeroSection.jsx';
import AboutSection from './components/AboutSection.jsx';
import ServicesSection from './components/ServicesSection.jsx';
import TestimonialsSection from './components/TestimonialsSection.jsx';
import ContactSection from './components/ContactSection.jsx';
import Footer from './components/Footer.jsx';

// Main App Component
function App() {
    // Function for smooth scrolling to a specific section ID
    const smoothScroll = (id) => {
        const element = document.getElementById(id);
        if (element) {
            // Scroll to the element with smooth behavior
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        // The main container for the entire application, applying global styles
        <div className="bg-gray-50 text-gray-800 min-h-screen">
            {/* Render the Header component, passing the smoothScroll function as a prop */}
            <Header smoothScroll={smoothScroll} />
            <main>
                {/* Render the HeroSection, passing the smoothScroll function */}
                <HeroSection smoothScroll={smoothScroll} />
                {/* Render the AboutSection */}
                <AboutSection />
                {/* Render the ServicesSection */}
                <ServicesSection />
                {/* Render the TestimonialsSection */}
                <TestimonialsSection />
                {/* Render the ContactSection */}
                <ContactSection />
            </main>
            {/* Render the Footer component */}
            <Footer />
        </div>
    );
}

export default App; // Export the App component as the default
